// PlayerNameEntry.js

import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';


const PlayerNameEntry = ({ numberOfPlayers, onNamesSubmit }) => {
  const [playerNames, setPlayerNames] = useState(Array(numberOfPlayers).fill(''));
  const history = useHistory();

  const handleInputChange = (index, event) => {
    const newPlayerNames = [...playerNames];
    newPlayerNames[index] = event.target.value;
    setPlayerNames(newPlayerNames);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    onNamesSubmit(playerNames);
    history.push('/game');
  };

  return (
    <div>
      <h2>Enter Player Names</h2>
      <form onSubmit={handleSubmit}>
        {playerNames.map((name, index) => (
          <div key={index}>
            <label>Player {index + 1}:</label>
            <input
              type="text"
              value={name}
              onChange={(event) => handleInputChange(index, event)}
              required
            />
          </div>
        ))}
        <button type="submit">Start Game</button>
      </form>
    </div>
  );
};

export default PlayerNameEntry;
